import React from "react";
import Button from "@material-ui/core/Button";
import TextField from "@material-ui/core/TextField";
import Dialog from "@material-ui/core/Dialog";
import DialogActions from "@material-ui/core/DialogActions";
import DialogContent from "@material-ui/core/DialogContent";
import DialogContentText from "@material-ui/core/DialogContentText";
import DialogTitle from "@material-ui/core/DialogTitle";
import Typography from "@material-ui/core/Typography";
import Slider from "@material-ui/core/Slider";
import Input from "@material-ui/core/Input";
import MuiAlert from "@material-ui/lab/Alert";
import { makeStyles } from "@material-ui/core/styles";

const useStyles = makeStyles((theme) => ({
  unitsForm: {
    margin: theme.spacing(2, 0),
  },
  sliderHolder: {
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
  },
  slider: {
    width: theme.spacing(30),
  },
}));

export default function OrderDialog({ posting, open, handleClose }) {
  const classes = useStyles();

  const [name, setName] = React.useState("");
  const [address, setAddress] = React.useState("");
  const [units, setUnits] = React.useState(0);
  const handleSliderChange = (event, newValue) => {
    setUnits(newValue);
  };

  const handleInputChange = (event) => {
    setUnits(event.target.value === "" ? "" : Number(event.target.value));
  };

  const handleBlur = () => {
    if (units < 0) {
      setUnits(0);
    } else if (units > posting.units) {
      setUnits(posting.units);
    }
  };

  /*----Alert Conditions----*/
  const [noName, setNoName] = React.useState(false);

  /*----Handle Alerts----*/
  const handleNoTitle = () => {
    setNoName(true);
  };

  const handleAlertClose = () => {
    setNoName(false);
  };

  return (
    <div>
      <Dialog
        open={open}
        onClose={handleClose}
        aria-labelledby="form-dialog-title"
        maxWidth="xs"
      >
        <DialogTitle id="form-dialog-title">Order Form</DialogTitle>
        <DialogContent>
          <DialogContentText>
            Please enter your name, address and number of units that you would
            like to order.
          </DialogContentText>
          <TextField
            autoFocus
            margin="dense"
            id="name"
            label="Name"
            type="name"
            fullWidth
          />
          <TextField
            autoFocus
            margin="dense"
            id="address"
            label="Address"
            type="address"
            fullWidth
          />
          <div className={classes.unitsForm}>
            <Typography color="textSecondary">Units</Typography>
            <div className={classes.sliderHolder}>
              <Slider
                className={classes.slider}
                defaultValue={0}
                value={typeof units === "number" ? units : 0}
                onChange={handleSliderChange}
                aria-labelledby="input-slider"
                min={0}
                max={posting.units}
                valueLabelDisplay="auto"
                getAriaValueText={units + "kg"}
                valueLabelFormat={units + "kg"}
              />
            </div>
          </div>
        </DialogContent>
        <DialogActions>
          <Button onClick={handleClose} color="secondary">
            Cancel
          </Button>
          <Button onClick={handleClose} color="primary">
            Place Order
          </Button>
        </DialogActions>
      </Dialog>
    </div>
  );
}
