import React, { Component } from "react";
import { withStyles } from "@material-ui/core/styles";
import Container from "@material-ui/core/Container";
import Card from "@material-ui/core/Card";
import CardContent from "@material-ui/core/CardContent";
import CardMedia from "@material-ui/core/CardMedia";
import Typography from "@material-ui/core/Typography";
import CssBaseline from "@material-ui/core/CssBaseline";

const basePostings = [
  {
    postingId: 1,
    username: "James",
    nameOfProduct: "Fresh Arabica Coffee Beans",
    units: 100,
    pricePerUnit: 35,
    image: "https://source.unsplash.com/G88j9KT5u4g/1600x900",
    tags: {
      beanType: "arabica",
      roastLevel: "light",
      organic: false,
    },
    description: "Fresh Arabica Coffee Beans from Brazil.",
    pending: [],
    confirmed: [],
    delivered: [],
  },
  {
    postingId: 2,
    username: "Oliver",
    nameOfProduct: "Robusta Beans",
    units: 120,
    pricePerUnit: 20,
    image: "https://source.unsplash.com/PMnJWQ1F_ww/1600x900",
    tags: {
      beanType: "robusta",
      roastLevel: "dark",
      organic: true,
    },
    description: "These beans were freshly harvested in India. 100% Organic",
    pending: [],
    confirmed: [],
    delivered: [],
  },
  {
    postingId: 3,
    username: "James",
    nameOfProduct: "Kopi Nganu",
    units: 50,
    pricePerUnit: 50,
    image: "https://source.unsplash.com/tvVkydhyspU/1600x900",
    tags: {
      beanType: "others",
      roastLevel: "",
      organic: true,
    },
    description: "Fresh from Indonesia. While stocks last.",
    pending: [],
    confirmed: [],
    delivered: [],
  },
];

const useStyles = (theme) => ({
  root: {
    display: "flex",
    height: theme.spacing(80),
    margin: theme.spacing(5),
    backgroundColor: theme.palette.background.paper,
  },
  details: {
    display: "flex",
    flexDirection: "column",
  },
  content: {
    flex: "1 0 auto",
  },
  cover: {
    width: "45%",
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
  },
  controls: {
    display: "flex",
    alignItems: "center",
    paddingLeft: theme.spacing(1),
    paddingBottom: theme.spacing(1),
  },
  image: {
    width: theme.spacing(50),
    height: theme.spacing(40),
  },
  // detailsHolder: {
  //   width: "50%",
  //   float: "right",
  //   height: "50%",
  //   display: "flex",
  //   justifyContent: "center",
  //   alignItems: "center",
  // },
});

class Posting extends Component {
  constructor(props) {
    super(props);

    this.state = {
      postingId: props.match.params.postingId,
      posting: basePostings.find((posting) =>
        String(posting.postingId).match(props.match.params.postingId)
      ),
    };
  }

  render() {
    const posting = this.state.posting;
    const { classes } = this.props;

    return (
      <React.Fragment>
        <CssBaseline />
        <Container maxWidth="lg" className={classes.container}>
          <Card className={classes.root}>
            {/* <CardMedia
              className={classes.cover}
              image={posting.image}
              title={posting.name}
            ></CardMedia> */}
            <div className={classes.cover}>
              <img
                className={classes.image}
                style={{ display: posting.image }}
                src={posting.image}
              />
            </div>
            <div className={classes.details}>
              <CardContent className={classes.content}>
                <Typography component="h5" variant="h5">
                  {posting.nameOfProduct}
                </Typography>
                <Typography variant="subtitle1" color="textSecondary">
                  Posted by {posting.username}
                </Typography>
                <div></div>
              </CardContent>
              <div className={classes.controls}></div>
            </div>
          </Card>
        </Container>
      </React.Fragment>
    );
  }
}

export default withStyles(useStyles)(Posting);
