import React, { Component } from "react";
import { withStyles } from "@material-ui/core/styles";
import Container from "@material-ui/core/Container";
import Paper from "@material-ui/core/Paper";

const basePostings = [
  {
    postingId: 1,
    username: "James",
    nameOfProduct: "Fresh Arabica Coffee Beans",
    units: 100,
    pricePerUnit: 35,
    image: "https://source.unsplash.com/G88j9KT5u4g/1600x900",
    tags: {
      beanType: "arabica",
      roastLevel: "light",
      organic: false,
    },
    description: "Fresh Arabica Coffee Beans from Brazil.",
    pending: [],
    confirmed: [],
    delivered: [],
  },
  {
    postingId: 2,
    username: "Oliver",
    nameOfProduct: "Robusta Beans",
    units: 120,
    pricePerUnit: 20,
    image: "https://source.unsplash.com/PMnJWQ1F_ww/1600x900",
    tags: {
      beanType: "robusta",
      roastLevel: "dark",
      organic: true,
    },
    description: "These beans were freshly harvested in India. 100% Organic",
    pending: [],
    confirmed: [],
    delivered: [],
  },
  {
    postingId: 3,
    username: "James",
    nameOfProduct: "Kopi Nganu",
    units: 50,
    pricePerUnit: 50,
    image: "https://source.unsplash.com/tvVkydhyspU/1600x900",
    tags: {
      beanType: "others",
      roastLevel: "",
      organic: true,
    },
    description: "Fresh from Indonesia. While stocks last.",
    pending: [],
    confirmed: [],
    delivered: [],
  },
];

const useStyles = (theme) => ({
  root: {},
  imageHolder: {
    width: "50%",
    float: "left",
    height: "100%",
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
  },
  image: {
    position: "relative",
    backgroundColor: theme.palette.grey[800],
    color: theme.palette.common.white,
    margin: theme.spacing(5, 0),
    height: theme.spacing(75),
    //backgroundImage: "url(https://source.unsplash.com/random)",
    backgroundSize: "cover",
    backgroundRepeat: "no-repeat",
    backgroundPosition: "center",
    // [theme.breakpoints.down("md")]: {
    //   height: theme.spacing(45),
    // },
    // [theme.breakpoints.down("xs")]: {
    //   height: theme.spacing(45),
    // },
  },
  detailsHolder: {
    width: "50%",
    float: "right",
    height: "100%",
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
  },
});

class Posting extends Component {
  constructor(props) {
    super(props);

    this.state = {
      postingId: props.match.params.postingId,
      posting: basePostings.find((posting) =>
        String(posting.postingId).match(props.match.params.postingId)
      ),
    };
  }

  // componentDidMount() {
  //   let userPostings = basePostings;
  //   const inputName = this.state.name.toLowerCase();
  //   userPostings = userPostings.filter((posting) =>
  //     posting.username.toLowerCase().match(inputName)
  //   );
  //   this.setState({ postings: userPostings });
  // }

  render() {
    const { classes } = this.props;
    return (
      <React.Fragment>
        <Container maxWidth="md" className={classes.root}>
          <div className={classes.imageHolder}>
            {/* <Paper
              className={classes.image}
              style={{ backgroundImage: `url(${this.state.posting.image})` }}
              backgroundImage={this.state.posting.image}
            ></Paper> */}
          </div>
          <div className={classes.detailsHolder}>Details</div>
        </Container>
        {/* Footer */}
      </React.Fragment>
    );
  }
}

export default withStyles(useStyles)(Posting);
