import React from "react";
import Button from "@material-ui/core/Button";
import TextField from "@material-ui/core/TextField";
import Dialog from "@material-ui/core/Dialog";
import DialogActions from "@material-ui/core/DialogActions";
import DialogContent from "@material-ui/core/DialogContent";
import DialogContentText from "@material-ui/core/DialogContentText";
import DialogTitle from "@material-ui/core/DialogTitle";
import Grid from "@material-ui/core/Grid";
import Typography from "@material-ui/core/Typography";
import Slider from "@material-ui/core/Slider";
import Input from "@material-ui/core/Input";
import MuiAlert from "@material-ui/lab/Alert";
import { makeStyles } from "@material-ui/core/styles";

const useStyles = makeStyles({
  root: {
    width: 100,
  },
  input: {
    width: 42,
  },
});

export default function OrderDialog({ posting, open, handleClose }) {
  //   const [open, setOpen] = React.useState(false);
  const [name, setName] = React.useState("");
  const [address, setAddress] = React.useState("");
  const [units, setUnits] = React.useState(0);

  const handleSliderChange = (event, newValue) => {
    setUnits(newValue);
  };

  const handleInputChange = (event) => {
    setUnits(event.target.value === "" ? "" : Number(event.target.value));
  };

  const handleBlur = () => {
    if (units < 0) {
      setUnits(0);
    } else if (units > 100) {
      setUnits(100);
    }
  };

  /*----Alert Conditions----*/
  const [noName, setNoName] = React.useState(false);

  //   const handleClickOpen = () => {
  //     setOpen(true);
  //   };

  //   const handleClose = () => {
  //     setOpen(false);
  //   };
  /*----Handle Alerts----*/
  const handleNoTitle = () => {
    setNoName(true);
  };

  const handleAlertClose = () => {
    setNoName(false);
  };

  return (
    <div>
      <Dialog
        open={open}
        onClose={handleClose}
        aria-labelledby="form-dialog-title"
      >
        <DialogTitle id="form-dialog-title">Order Form</DialogTitle>
        <DialogContent>
          <DialogContentText>
            Please enter your name, address and number of units that you would
            like to order.
          </DialogContentText>
          <TextField
            autoFocus
            margin="dense"
            id="name"
            label="Name"
            type="name"
            fullWidth
          />
          <TextField
            autoFocus
            margin="dense"
            id="address"
            label="Address"
            type="address"
            fullWidth
          />
          <Slider
            value={typeof units === "number" ? units : 0}
            onChange={handleSliderChange}
            aria-labelledby="input-slider"
          />
        </DialogContent>
        <DialogActions>
          <Button onClick={handleClose} color="primary">
            Cancel
          </Button>
          <Button onClick={handleClose} color="primary">
            Place Order
          </Button>
        </DialogActions>
      </Dialog>
    </div>
  );
}
